import React, { useLayoutEffect } from 'react';
import ReactDOM from 'react-dom/client';
import ToDoItem from './toDoItem';
import ToDoList from './ToDoList';


const root = ReactDOM.createRoot(document.getElementById('root'));



class App extends React.Component{
constructor(props){
  super(props);

  this.state = {
    todos :[
      {id:1, title: 'Todo 1', done: false},
      {id:2, title: 'Todo 2', done: true},
      {id:3, title: 'Todo 3', done: false},
      {id:4, title: 'Todo 4', done: true},
      {id:5, title: 'Todo 5', done: false},
    ]
  };
}

handleClick = (id) => {
 const newState =  this.state.todos.map((todo) => {
    if(todo.id == id){
      todo.done = !todo.done;
    }
    return todo;
  })
  this.setState({todos: newState});
}

  render(){
    return (
    <>

      <h1>{this.props.title} items count: {this.state.todos.length}</h1>
      <ToDoList>
        {this.state.todos.map((todo) => {
          return <ToDoItem key={todo.id} id={todo.id} title={todo.title} done={todo.done} onClick={this.handleClick}/>
        })}
      </ToDoList>

    </>
    );
  }
}

App.defaultProps = {
  title: "i am a defoult title"
}

root.render(<App title = "To Do App" />);
