import React, { useState } from "react";
import iconCheck from "./images/icon-check.svg"
import iconCross from "./images/icon-cross.svg"
import styles from "./style";

const TodoList = () =>{
  const todoItems=[{content:"Jog around the park 3x", done:false},{content:"10 minutes meditation", done:false},{content:"Read for 1 hour", done:false},{content:"Pick up groceries", done:false}];

  const List = todoItems.map((todo,index ) => 
  <li style={styles.inputDiv} key={index} id={index}>
    <div className="checkbox-text">
      <div style={!todo.done ? styles.checkBox : styles.checkBoxChecked}>
        <img src={iconCheck}/>
      </div>
      <span>{todo.content}</span>
    </div>
    <img className="CrossImg" src={iconCross}/>
  </li>  
  )

  return (
    <List />
   )
}

export default TodoList;
