import React, { useState, useTransition } from "react";
import iconCheck from "./images/icon-check.svg"
import iconCross from "./images/icon-cross.svg"
import styles from "./style";

const TodoList = ({todoItems}) =>{
  const {ifdone,setifdone} = useState(todoItems);
  const handleClick =(index)=>{
   setifdone(todoItems[index].done = !todoItems[index].done )
  }
  return (
    todoItems.map((todo,index ) => 
    <li onClick={() =>handleClick(index)} style={styles.inputDiv} key={index} id={index}>
      <div className="checkbox-text">
        <div style={!todo.done ? styles.checkBox : styles.checkBoxChecked}>
          <img src={iconCheck}/>
        </div>
        <span>{todo.content}</span>
      </div>
      <img className="CrossImg" src={iconCross}/>
    </li>  
    )
   )
}

export default TodoList;
