import React, { useState } from "react";
import iconCheck from "./images/icon-check.svg"
import iconCross from "./images/icon-cross.svg"
import styles from "./style";


const TodoList = ({todoItems,setTodoItems,lists}) =>{
  const list = [...todoItems];

  const doneUndone =(index)=>{
    list[index].done = !list[index].done
    console.log(list)

   setTodoItems(list);
  }

  const remove = (id) => {
    list.splice(id,1);
    todos.splice(id,1)
    setTodoItems(list);
  }

  const liList = () =>{
    if(lists == 'complated'){
      return todos.filter((item) => {
        if(item.done === true){
          return item;
        }
      })
    }else if(lists == 'active'){
      return todoItems.filter((item) => {
        if(item.done === false){
          return item;
        }
      })
    }else{
      return todoItems
    }
  }

  return (
    liList.map((todo,index) => 
    <li  style={styles.inputDiv} key={index} id={index}>
      <div onClick={() =>doneUndone(index)} className="checkbox-text">
        <div style={!todo.done ? styles.checkBox : styles.checkBoxChecked}>
          <img src={iconCheck}/>
        </div>
        <span style={!todo.done ? styles.liContent : styles.liContentChecked}>{todo.content}</span>
      </div>
      <img onClick={() => remove(index)} className="CrossImg" src={iconCross}/>
    </li>  
    )
   )
}

export default TodoList;
