import React, { useLayoutEffect } from 'react';
import ReactDOM from 'react-dom/client';
import ToDoItem from './toDoItem';
import ToDoList from './ToDoList';


const root = ReactDOM.createRoot(document.getElementById('root'));



class App extends React.Component{
todos = [
    {id:1, title: 'Todo 1', done: false},
    {id:2, title: 'Todo 2', done: true},
    {id:3, title: 'Todo 3', done: false},
    {id:4, title: 'Todo 4', done: true},
    {id:5, title: 'Todo 5', done: false},
 ];
handleClick = (smth) => {
  console.log(smth);
}

  render(){
    return (
    <>

      <h1>{this.props.title} items count: 3</h1>
      <ToDoList>
        {this.todos.map((todo) => {
          return <ToDoItem id={todo.id} title={"Todo 1"} done={false} onClick={this.handleClick}/>
        })}
        <ToDoItem id={1} title={"Todo 1"} done={false} onClick={this.handleClick}/>
        <ToDoItem id={2} title={"Todo 2"} done={true} onClick={this.handleClick}/>
        <ToDoItem id={3} title={"Todo 3"} done={false} onClick={this.handleClick}/>
      </ToDoList>

    </>
    );
  }
}

root.render(<App title = "To Do App" />);
