import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';


const products =[ "Joint", "pipe", "satite", 'chaqache', "bong"]


const ShoppingCart = (props) =>{
  const[productList, setProducts] =useState(products);
  const [cartItems, setCartItems] =useState([]);

  const handleClick = (product) => {

  setCartItems((prevCart) => {
    return [...prevCart,product]
  })
  }

  const handleRemove = (productIndex) => {
    const newCart = cartItems.filter((product,index) =>{

      return productIndex !== index;
    });

    setCartItems(newCart);
  }

  return (
    <>
    <h1>{props.title}</h1>
    {productList.map((product,index) => {
      return <button key={index} onClick={() => handleClick(product)}>{product}</button>
    })}
    <h1>shopping Cart</h1>
    <ul>
      {cartItems.map((cartItem,index) => {
        return <li key={index} onClick={() => handleRemove(index)}>{cartItem}</li>
      })}
    </ul>

    </>
  ); 
}


const root = ReactDOM.createRoot(document.getElementById('root'));

root.render(<ShoppingCart title = "Coffee shop" />);
