import React from "react";
import ToDoItem from "./toDoItem";


class ToDoList extends React.Component{
  handleClick = (smth) => {
    console.log(smth);
  }
  render(){

    return (
      <div>
        <h1>{this.props.title} items count: {this.props.todos.length}</h1>
        <ul>
          {this.props.todos.map((todo) => {
            const {title,id,done} = todo;
          return <ToDoItem onClick={this.handleClick} title={title} key={id} done={done}/>
          })}
        </ul>
      </div>
    );
  }
}

export default ToDoList;
