import React, { useState } from "react";
import iconCheck from "./images/icon-check.svg"
import iconCross from "./images/icon-cross.svg"
import styles from "./style";

const TodoITems = (props) =>{
  const [TodoList,setTodoList] = useState(["Jog around the park 3x",'10 minutes meditation']);
  const handleclick = (id) =>{
    document.getElementById(`${id}`).firstElementChild.firstElementChild.style = styles.checkBoxChecked;
    console.log(document.getElementById(`${id}`).firstElementChild.firstElementChild.style)
  }

  return (
   [
    TodoList.map((todo,index ) => 
    <li checked={false} style={styles.inputDiv} key={index} id={index} onClick={() => handleclick(index)}>
      <div className="checkbox-text">
        <div style={checked ? styles.checkBox : styles.checkBoxChecked}>
          <img src={iconCheck}/>
        </div>
        <span>{todo}</span>
      </div>
      <img className="CrossImg" src={iconCross}/>
    </li>  
    )
   ])
}

export default TodoITems;
