import React from "react"
import ReactDOM from 'react-dom/client';
import bgImgMobileLite from "./images/bg-mobile-light.jpg"
import bgImgDark from "./images/bg-mobile-dark.jpg"
import iconCheck from "./images/icon-check.svg"
import moon from "./images/icon-moon.svg"
import { useState } from "react"
import TodoList from "./ToDoList"
import { keyboard } from "@testing-library/user-event/dist/keyboard";
import styles from "./style";

const MainPage = ()=>{
  const [todoItems,setTodoItems]=useState([{content:"Jog around the park 3x", done:false},{content:"10 minutes meditation", done:false},{content:"Read for 1 hour", done:false},{content:"Pick up groceries", done:false}]);
  const addTodoItem = () =>{
    let inputText = document.querySelector(".addTodo").value;
    let newTodoItems = [...todoItems];
    if(inputText !== ""){
      newTodoItems.unshift({content:inputText, done:false});
    }
    setTodoItems(newTodoItems);
    document.querySelector(".addTodo").value ="";
  }

  return (
    <>
      <img className='bg-img' src={bgImgMobileLite} alt=''/>
      <div className='header-icon'>
        <h1>TODO</h1>
        <img className='icon-moon' src={moon}/>
      </div>
      <div className='input-div'>
        <div style={styles.checkBox} onClick={addTodoItem}>
          <img src={iconCheck}/>
        </div>
        <input  className='addTodo' type='text' placeholder='Create a new todo' defaultValue={''}/>
      </div>
      <ul id="ul">
        <TodoList todoItems={todoItems} setTodoItems={setTodoItems}/>
      </ul>
      <div className='navigationDiv'>
        <span>{todoItems.length} items left</span>
        <span>clear complated</span>
      </div>

      </>)

}

export default MainPage;
