import React, { useState } from "react";
import iconCheck from "./images/icon-check.svg"
import iconCross from "./images/icon-cross.svg"
import styles from "./style";


const TodoList = ({todoItems,setTodoItems,filteredItems,liList}) =>{
  const list = [...todoItems];

  const doneUndone =(index)=>{
    list[index].done = !list[index].done

   setTodoItems(list);
  }

  const remove = (id) => {
    list.splice(id,1);
    setTodoItems(list);
  }

  const filter = () =>{
    if(filteredItems == 'complated'){
      liList = todoItems.filter((item) => {
        if(item.done === true){
          return item;
        }
      })
      return liList
    }else if(filteredItems == 'active'){
      liList = todoItems.filter((item) => {
        if(item.done === false){
          return item;
        }
      })
      return liList;
    }else{
      return liList = todoItems
    }
  }


  return (

    filter().map((todo,index) => 
    <li  style={styles.inputDiv} key={index} id={index}>
      <div onClick={() =>doneUndone(index)} className="checkbox-text">
        <div style={!todo.done ? styles.checkBox : styles.checkBoxChecked}>
          <img src={iconCheck}/>
        </div>
        <span style={!todo.done ? styles.liContent : styles.liContentChecked}>{todo.content}</span>
      </div>
      <img onClick={() => remove(index)} className="CrossImg" src={iconCross}/>
    </li>  
    )
   )
}

export default TodoList;
