import React, { useLayoutEffect } from 'react';
import ReactDOM from 'react-dom/client';

const root = ReactDOM.createRoot(document.getElementById('root'));

class ToDoItem extends React.Component{

  render(){
    return( 
      <li onClick={() => this.props.onClick(this.props.title)}> 
        {this.props.title} {(this.props.done) ? "✔️" : "❌"}
      </li>
    )
  }
}

class ToDoApp extends React.Component{

  render(){

    return (
      <div>
        <h1>{this.props.title} items count: {this.props.count}</h1>
        <ul>
          {this.props.todos.map((todo) => {
            const {title,id,done} = todo;
          return <ToDoItem onClick={this.props.onClick} title={title} key={id} done={done}/>
          })}
        </ul>
      </div>
    );
  }
}
const todos = [
  {id:1, title: 'Todo 1', done: false},
  {id:2, title: 'Todo 2', done: true},
  {id:3, title: 'Todo 3', done: false},
  {id:4, title: 'Todo 4', done: true},
  {id:5, title: 'Todo 5', done: false},
];

const handleClick = (smth) => {
  console.log(smth);
}

root.render(<ToDoApp onClick={handleClick} title = "To Do App" count = {todos.length} todos = {todos}/>);
