import React, { useState } from "react";
import iconCheck from "./images/icon-check.svg"
import iconCross from "./images/icon-cross.svg"
import styles from "./style";


const TodoList = ({todoItems,setTodoItems,filterValue,liList}) =>{
  const list = [...todoItems];

  const doneUndone =(id)=>{
    list.map((item,index) => {
      if(item.id == id){
        list[index].done = !list[index].done
        console.log(index);
      }
    })

   setTodoItems(list);
  }

  const remove = (id) => {
    list.splice(id,1);
    setTodoItems(list);
  }

  liList = [];
  const filter = () =>{
    if(filterValue == 'complated'){
      liList = todoItems.filter((item) => {
        if(item.done === true){
          return item;
        }
      })
      return liList
    }else if(filterValue == 'active'){
      liList = todoItems.filter((item) => {
        if(item.done === false){
          return item;
        }
      })
      return liList;
    }else{
      return liList = todoItems;
    }
  }
  console.log(todoItems)

  return (

    filter().map((todo,index) => 
    <li  style={styles.inputDiv} key={todo.content} id={todo.content}>
      <div onClick={() =>doneUndone(todo.content)} className="checkbox-text">
        <div style={!todo.done ? styles.checkBox : styles.checkBoxChecked}>
          <img src={iconCheck}/>
        </div>
        <span style={!todo.done ? styles.liContent : styles.liContentChecked}>{todo.content}</span>
      </div>
      <img onClick={() => remove(todo.id)} className="CrossImg" src={iconCross}/>
    </li>  
    )
   )
}

export default TodoList;
