import React from "react"
import ReactDOM from 'react-dom/client';
import bgImgMobileLite from "./images/bg-mobile-light.jpg"
import bgImgDark from "./images/bg-mobile-dark.jpg"
import iconCheck from "./images/icon-check.svg"
import moon from "./images/icon-moon.svg"
import { useState } from "react"
import TodoList from "./ToDoList"
import { keyboard } from "@testing-library/user-event/dist/keyboard";
import styles from "./style";

const todos = [{content:"Jog around the park 3x", done:false},{content:"10 minutes meditation", done:false},{content:"Read for 1 hour", done:false},{content:"Pick up groceries", done:false},{content:'Complete Todo App on Frontend Mentor', done:false}];
let lists = 'all';

const MainPage = ()=>{
  let liList=[];
  const [todoItems,setTodoItems]=useState(todos);
  const [filteredItems, setFilteredItems]=useState('all')

  const addTodoItem = () =>{
    let inputText = document.querySelector(".addTodo").value;
    let newTodoItems = [...todoItems];
    if(inputText !== ""){
      newTodoItems.unshift({content:inputText, done:false});
      todos.unshift({content:inputText, done:false});
      console.log(newTodoItems);
    }
    setTodoItems(newTodoItems);
    document.querySelector(".addTodo").value ="";
  }

  const clearComplated = () =>{
    setTodoItems(todoItems.filter((item) => {
      if(item.done === false){
        return item;
      }
    }))
  }

  const activeTodos = () => {
    setFilteredItems('active')
  }

  const showAllTodos = () =>{
    setFilteredItems('all')
  }

  const complatedTodos = () =>{
    setFilteredItems('complated')
  }


  return (
    <>
      <img className='bg-img' src={bgImgMobileLite} alt=''/>
      <div className='header-icon'>
        <h1>TODO</h1>
        <img className='icon-moon' src={moon}/>
      </div>
      <div className='input-div'>
        <div style={styles.checkBox} onClick={addTodoItem}>
          <img src={iconCheck}/>
        </div>
        <input  className='addTodo' type='text' placeholder='Create a new todo' defaultValue={''}/>
      </div>
      <ul id="ul">
        <TodoList todoItems={todoItems} setTodoItems={setTodoItems} filteredItems={filteredItems} liList={liList}/>
      </ul>
      <div className='navigationDiv'>
        <span>{liList.length} items left</span>
        <span onClick={clearComplated}>clear complated</span>
      </div>
      <div className="navigationDiv secondNavigationDiv">
        <span onClick={showAllTodos} >All</span>
        <span onClick={activeTodos}>Active</span>
        <span onClick={complatedTodos}>Complated</span>
      </div>

      </>)

}

export default MainPage;
